import React, { useState } from 'react';
import lgc from '../lgc.png';
import './Navbar.css';
import ItemList from './Items';

function Navbar() {
    const [searchValue, setSearchValue] = useState('');
    let currencies = ["USD", "JPY", "GBP", "AUD"];
    const handleInputChange = (event) => {
        setSearchValue(event.target.value);
    };

    return (
        <div className="ew">
            <div className="Navbar">
                <img src={lgc} className="logoNavbar" alt="logo" />
                <div className="dropdown-center">
                    <button className="btn btn-secondary dropdown-toggle" type="button" data-bs-toggle="dropdown" aria-expanded="false">
                        Centered dropdown
                    </button>
                    <ul className="dropdown-menu">
                        <li><a className="dropdown-item" href="#">Action</a></li>
                        <li><a className="dropdown-item" href="#">Action two</a></li>
                        <li><a className="dropdown-item" href="#">Action three</a></li>
                    </ul>
                </div>
                <div className="Searchbar">
                    <input
                        type="text"
                        placeholder="Que cherchez-vous?"
                        className="input-group-text"
                        value={searchValue}
                        onChange={handleInputChange}
                    />
                </div>
                <div className='spacer'></div>

            </div>
            <ItemList myFilter={searchValue} />
        </div >
    );
}

export default Navbar;
