import React, { useState, useEffect } from 'react';
import './App.css';

const Modal = ({ onClose, name, description, seller, photo, price, currency }) => {
    const [amountInDollars, setAmountInDollars] = useState(null);

    useEffect(() => {
        const fetchExchangeRate = async () => {
            try {
                const apiKey = '018861a566f6c689570ec619';
                const baseCurrency = 'EUR';
                const apiUrl = `https://v6.exchangerate-api.com/v6/${apiKey}/pair/${baseCurrency}/${currency}`;
                const response = await fetch(apiUrl);
                const data = await response.json();

                if (data.result === 'success') {
                    const exchangeRate = data.conversion_rate;
                    const amountInDollars = price * exchangeRate;
                    setAmountInDollars(amountInDollars);
                } else {
                    throw new Error('Exchange rate fetching failed');
                }
            } catch (error) {
                console.error('Error fetching exchange rate:', error);
            }
        };

        fetchExchangeRate();
    }, [currency, price]);

    return (
        <div className="modal-overlay" onClick={onClose}>
            <div className="modal-content" onClick={(e) => e.stopPropagation()}>
                <span className="close-btn" onClick={onClose}>&times;</span>
                <div className="modal-img-container">
                    <img src={photo} alt="modal" />
                </div>
                <div className="modal-details">
                    <h5>{name}</h5>
                    <p>{description}</p>
                    {amountInDollars !== null && !isNaN(amountInDollars) && (
                        <p>Price: {Math.round(amountInDollars)} {currency}</p>
                    )}
                    <p>Sold by: <b>{seller}</b></p>
                </div>
            </div>
        </div>
    );
};

export default Modal;
