import React, { useState, useEffect } from 'react';
import './Items.css';
import Modal from './Modal';
import { createClient } from '@supabase/supabase-js'

const supabaseUrl = 'https://eblwtaeglbtxppddyygp.supabase.co'
const supabaseKey = "eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJpc3MiOiJzdXBhYmFzZSIsInJlZiI6ImVibHd0YWVnbGJ0eHBwZGR5eWdwIiwicm9sZSI6ImFub24iLCJpYXQiOjE3MDY4NzQzNTMsImV4cCI6MjAyMjQ1MDM1M30.6t0_jPNYubLCPmEl8TrK8GCG8g4QRp1mSUejzcMLPH8"
const supabase = createClient(supabaseUrl, supabaseKey)

function convertCurrency(price, targetCurrency) {
    const apiKey = '096b83cdff65a29422914cc5';
    const baseCurrency = 'EUR';
    const apiUrl = `https://v6.exchangerate-api.com/v6/${apiKey}/pair/${baseCurrency}/${targetCurrency}`;

    return fetch(apiUrl)
        .then(response => response.json())
        .then(data => {
            if (data.result === 'success') {
                const exchangeRate = data.conversion_rate;
                const amountInDollars = price * exchangeRate;
                return amountInDollars;
            } else {
                throw new Error('Currency conversion failed');
            }
        });
}

function Items({ name, description, seller, photo, price, onClick, myCurrency }) {
    const [amountInDollars, setAmountInDollars] = useState(null);

    useEffect(() => {
        const fetchData = async () => {
            try {
                const result = await convertCurrency(price, myCurrency);
                setAmountInDollars(result);
            } catch (error) {
                console.error('Error converting currency:', error);
            }
        };

        fetchData();
    }, [price, myCurrency]);

    return (
        <div className="Item" onClick={onClick}>
            <div className="card">
                <div className="imgContainer">
                    <img src={photo} className="card-img-top" alt="..." />
                </div>
                <div className="card-body">
                    <h5 className="card-title">{name}</h5>
                    <p className="card-text">{description}</p>
                    {amountInDollars !== null && (
                        <p>Price: {Math.round(amountInDollars)} {myCurrency}</p>
                    )}
                    <div className="seller">
                        Sold by: <b> {seller}</b>
                    </div>
                </div>
            </div>
        </div>
    );
}

function ItemList({ myFilter, myCurrency }) {
    const [articles, setArticles] = useState([]);
    const [selectedItem, setSelectedItem] = useState(null);

    useEffect(() => {
        const fetchArticles = async () => {
            const { data } = await supabase.from("article").select("*");
            setArticles(data);
        };

        fetchArticles();
    }, []);



    const filteredArticles = myFilter
        ? articles.filter(article =>
            article.name.toLowerCase().includes(myFilter.toLowerCase()) ||
            article.description.toLowerCase().includes(myFilter.toLowerCase()) ||
            article.seller.toLowerCase().includes(myFilter.toLowerCase())
        )
        : articles;

    return (
        <div className='ItemList'>
            {filteredArticles.map((article, index) => (
                <Items
                    key={index}
                    name={article.name}
                    description={article.description}
                    seller={article.seller}
                    photo={article.photo}
                    price={article.price}
                    onClick={() => handleItemClick(article)}
                    myCurrency={myCurrency}
                />
            ))}
            {selectedItem && (
                <Modal
                    onClose={handleCloseModal}
                    name={selectedItem.name}
                    description={selectedItem.description}
                    seller={selectedItem.seller}
                    photo={selectedItem.photo}
                    price={selectedItem.price}
                />
            )}
        </div>
    );
}

export default ItemList;    const handleItemClick = (article) => {
        setSelectedItem(article);
    };

    const handleCloseModal = () => {
        setSelectedItem(null);
    };
