import React, { useState, useEffect } from 'react';
import './Modal.css';

function convertCurrency(price) {
    const apiKey = '096b83cdff65a29422914cc5';
    const baseCurrency = 'EUR';
    const targetCurrency = 'USD';
    const apiUrl = `https://v6.exchangerate-api.com/v6/${apiKey}/latest/${baseCurrency}`;

    return fetch(apiUrl)
        .then(response => response.json())
        .then(data => {
            const exchangeRate = data.conversion_rates[targetCurrency];
            const amountInDollars = price * exchangeRate;
            console.log(amountInDollars);
            return amountInDollars;
        })
        .catch(error => {
            console.error('Erreur lors de la conversion de la devise :', error);
            throw error;
        });
}

const Modal = ({ onClose, name, description, seller, photo, price }) => {
    const [amountInDollars, setAmountInDollars] = useState(null);

    useEffect(() => {
        convertCurrency(price)
            .then(result => setAmountInDollars(result))
            .catch(error => console.error('Error in useEffect:', error));
    }, [price]);

    return (
        <div className="modal-overlay" onClick={onClose}>
            <div className="modal-content" onClick={(e) => e.stopPropagation()}>
                <span className="close-btn" onClick={onClose}>&times;</span>
                <div className="modal-img-container">
                    <img src={photo} alt="modal" />
                </div>
                <div className="modal-details">
                    <h5>{name}</h5>
                    <p>{description}</p>
                    <p>Price: {price} â‚¬, {amountInDollars !== null ? `${amountInDollars} $` : 'Loading...'}</p>
                    <p>Sold by: <b>{seller}</b></p>
                </div>
            </div>
        </div>
    );
};

export default Modal;
